.hdr-box{
    width:14.5cm;
    position: relative;
    
    bottom:0.2cm;
    
    
}


/* Logo */
.hdr-left {
  padding-top: 35px;
  display: flex;
  justify-content: center;    
  align-items: center;        
  font-size: 9px;
  white-space: nowrap;
}

/* OR using position */
.hdr-left {
  position: relative;
  top: 30px;
  left:10px;
  
}


/* Middle details */
/* Middle details wrapper */
.hdr-center {
  width: 11cm;
  border-collapse: collapse; 
  margin: 0;
  padding: 0;
  overflow: hidden;
}

/* Inner table full width */
.hdr-inner {
  width: 14.1cm;          /* extend full width */
  border-collapse: collapse;
  table-layout: fixed;  /* makes width distribution consistent */
}

.hdr-center td,
.hdr-center th {
  padding: 2px 4px;      /* small padding for readability */
  margin: 0;
  vertical-align: top;  
  line-height: 1.2;
}

/* Left column */
.hdr-col-left {
  border: 1px solid black;
  border-right: none;
  font-size: 10px;
  line-height: 1.2;
  width: 4.8cm;   /* wider */
}

/* Right column */
.hdr-col-right {
  border: 1px solid black;
  border-left: none;
  border-right: none;
  font-size: 10px;
  line-height: 1.2;
  width: 4.8cm;   /* also wider */
}

/* Barcode cell */

@font-face {
    font-family: 'Code39';
    src: url('/assets/fonts/code39.eot'); /* IE9 Compat Modes */
    src: url('/assets/fonts/code39.eot?#iefix') format('embedded-opentype'), /* IE6-IE8 */
         url('/assets/fonts/code39.woff2') format('woff2'), /* Super modern browsers */
         url('/assets/fonts/code39.woff') format('woff'),   /* Modern browsers */
         url('/assets/fonts/code39.ttf') format('truetype'), /* Safari, Android, iOS */
         url('/assets/fonts/code39.svg#Code39') format('svg'); /* Legacy iOS */
    font-weight: normal;
    font-style: normal;
}




.hdr-barcode {
  border: 1px solid black;
  border-left: none;
  width: 2.8cm;   /* reserved for barcode */
  text-align: center;
  vertical-align: middle;
}

.barcode {
  position:relative;
  right:1.5cm;
  width: 3.5cm;
  height: 0.8cm;
}




/* Key-value layout */
.kv {
  display: flex;
  justify-content: flex-start;
  align-items: baseline;
  margin: 2px 0;
  padding: 0;
}

.k {
  margin-right: 4px;
  min-width: 70px;
}

.sep {
  margin: 0 2px;
}

.v {
  flex: .7;
}
.kv, .k, .sep, .v, .inline-right {
  margin: 0;
  padding: 0;
  line-height: 1.2;  
}

.inline-right {
  
  margin-left: 8px;
}










.rect2{
   height:7cm;
  width:18cm;
  border: 1px solid #333;
  
}


.item-table {   
  height:7cm;
  width:18.6cm;
  border-collapse: collapse;
  font-size: 11px;             
  font-family: Arial, sans-serif;
  border: 1px solid #333; 
  text-align: center;
  table-layout: fixed;
  border-spacing: 0 0.2cm;
}

.item-table th {
  line-height: 0;         
  font-size: 11px;            
  text-align: center;
  border-left: 1px solid #333;
  border-right: 1px solid #333;
  border-bottom: 1px solid #333;
  color:black;
  padding-bottom:0.2cm;
          
}

.item-table td {
  line-height: .8;          
  font-size: 11px; 
  border-left: 1px solid black;
  border-right: 1px solid black;
  border-bottom:none;
  padding:0 !important;
}
.item-table tr:nth-child(1){
     line-height: 1.5;
}



/* Optional: subtle styling for span inside description */
.item-table td span {
  font-size:10px;
}
.item-tabe td .test{
    font-size:10px;
}



/* Column widths based on your priority layout */
.item-table th:nth-child(1),
.item-table td:nth-child(1) {
  width: 1.5cm;/* Code */
}

.item-table th:nth-child(2),
.item-table td:nth-child(2) {
  width: 1cm; /* VAT */
}

.item-table th:nth-child(3),
.item-table td:nth-child(3) {
  width: 5.5cm; /* Description */
  padding-left:5px !important ;
}
.item-table td:nth-child(3){
    text-align:left;
    
}

.item-table th:nth-child(4),
.item-table td:nth-child(4) {
  width: 1.5cm; /* Qty */
  
}

.item-table th:nth-child(5),
.item-table td:nth-child(5) {
  width: 2.3cm; /* Unit Price */
  
}

.item-table th:nth-child(6),
.item-table td:nth-child(6) {
  width: 1.4cm; /* DISC % */
  
}

.item-table th:nth-child(7),
.item-table td:nth-child(7) {
  width: 2.2cm; /* Net Price */
  
}



.footer {
  display: flex;
  align-items: center;
  justify-content:center;
  margin: 0.2cm 0;
  width: 100%;
}
.final_table td{
    padding:0 !important;
}
