css:
/* General Page Setup */


.print-format {
  font-family: "Helvetica", "Arial", sans-serif;
  font-size: 12px;
  color: #333;
  line-height: 1.4;
  padding: 20px;
  margin: 0 -3mm;

}



.print-heading{
border-bottom:none;
position:relative;
top:1cm;

}

.print-heading h2 {
  text-align: left;
  font-size: 20px;
  margin: 0;
  color: #222;
}
.print-heading .sub-heading {
  display: block;
  font-size: 13px;
  color: #777;
  margin-top: 4px;
}

/* Section Titles */
.section-break label {
  font-weight: bold;
  color: #444;
}

/* Data Rows */
.data-field {
  margin: 4px 0;
  font-size: 12px;
}
.data-field label {
  font-weight: 600;
  color: #555;
}
.data-field .value {
  font-weight: 500;
  color: #222;
}

/* Two-column Layout Fix */
.column-break {
  padding: 5px 10px;
}

/* Summary Section in one line */
/* Make the summary section inline */

/* Make each data-field inline with no gaps */
[data-label="Summary"] .data-field {
    display: flex;
    flex-wrap: nowrap;        /* prevent label from breaking into 2 lines */
    align-items: center;      /* align label + value vertically */
    gap: 2px;                 /* small space between label and value */
    white-space: nowrap;
    margin-left:0.4cm;/* force text to stay in one line */
}

/* Reset the inner col-xs-12 blocks */
[data-label="Summary"] .data-field .col-xs-12 {
    display: inline; 
    width: auto !important;
    padding: 0 !important;
    margin: 0 !important;
}

/* Styling */
[data-label="Summary"] .data-field label {
    font-weight: bold;
    margin: 0;
}

[data-label="Summary"] .data-field .value {
    font-weight: normal;
    margin-left: 0.2cm !important;
}



[data-fieldname="naming_series"] {
  display:none !important;
}

[data-fieldname="custom_barcode"] .col-xs-7  {
position:relative;
bottom:1cm;
  width:70%;
  height:20%;
 
}
[data-fieldname="driver"]  {
position:relative;
bottom:1.5cm;
 
}



[data-fieldname="custom_barcode"] .col-xs-5 {
  display:none;
  
                 
}


/* Table Styling */
table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 10px;
}
table thead th {
  background: #f2f2f2;
  font-weight: bold;
  text-align: center;
  border: 1px solid #ccc;
  padding: 6px;
  font-size: 12px;
}
table tbody td {
  border: 1px solid #ccc;
  padding: 6px;
  font-size: 12px;
}
.table-sr {
  text-align: center;
  font-weight: 600;
}
.text-right {
  text-align: right;
}

/* Footer Page Numbers */
#footer-html {
  border-top: 1px solid #ccc;
  margin-top: 20px;
  padding-top: 8px;
}
#footer-html .page-number {
  font-size: 11px;
  color: #777;
}

#footer-html {
  border-top: 1px solid #ccc;
  margin-top: 20px;
  padding-top: 8px;
  page-break-after: avoid !important;
}

.print-format .page-break:last-child {
  page-break-after: auto !important;
}


[data-fieldname="workflow_state"] {
  display: none !important;
}

[data-fieldname="_last_child_rows"] {
  display: none !important;
}

[data-fieldname="delivery_route_item"] {
  text-align:center !important;
}




[data-fieldname="route_name"] label {
  color: transparent !important;   /* make original text invisible */
  position: relative;
}

[data-fieldname="route_name"] label::after {
  content: "Route Name:"; 
  position: absolute;
  left: 0; 
  top: 0;
  color: #444 !important;          /* visible replacement text */
  font-size: 12px;
  font-weight: bold;
}


th.table-sr {
padding-left:0.5cm!important;
  text-align: center;
}

th.table-sr::before {
  content: "#";
}

th.table-sr {
  color: transparent !important; /* hide "Sr" text itself */
}

th.text-right {
    position: relative;       /* make positioning work */
    color: transparent;       /* hide original text */
    background-color: transparent !important;
}

th.text-right::after {
    content: "Packs";  /* new text */
    position: absolute;
    left: 0;
    top: 0.25cm;
    width: 70%;
    height: 100%;
    color: #000;              /* visible color */
    text-align: right;        /* same as original alignment */
    font-weight: bold;        /* match table header style */
    display: flex;
    align-items: center;      /* vertically center */
    justify-content: flex-end; /* right align */
    padding-right: 6px;       /* match table padding */
    
}
